	private void btnLOAD_Click(object sender, System.EventArgs args)
	{
		// ** Place Event Handling Code Here **

		QueryDesignDataSet dataset = new QueryDesignDataSet();
		DynamicQuery query = new DynamicQuery(session.ConnectionPool);
		dataset = query.GetByID("CIC68322-DMRUsers");
		//dataset.QueryWhereItem[0].RValue = 123;
		//DataTable table = query.Execute(dataset).Tables["Results"];
		epiDmrUserList.DataSource = query.Execute(dataset).Tables["Results"];

	}


	private void Add_Click(ref DataTable dt, string el)
	{
		if(edvReasonDetail.Row == -1)
		{
			MessageBox.Show("Please load a reason first");
			return;
		}
        foreach (UltraGridRow row in epiDmrUserList.Selected.Rows)
		{
			DataRow r = dt.NewRow();
			r["Email"] = row.Cells[0].Value.ToString();
			dt.Rows.Add(r);
		}
        String catUsers = "";
        foreach (DataRow r in dt.Rows)
		{
            if (((string)r["Email"]).Length > 0)
			{
				if(catUsers.Length > 0) catUsers += "~";
                catUsers += (string)r["Email"];
			}
		}
        edvReasonDetail.dataView[edvReasonDetail.Row][el] = catUsers;
	}

	private void Del_Click(ref DataTable dt, string el, EpiUltraGrid ug)
	{
		if(edvReasonDetail.Row == -1)
		{
			MessageBox.Show("Please load a reason first");
			return;
		}
		string emaillist = string.Empty;
        foreach (UltraGridRow row in ug.Selected.Rows)emaillist += (emaillist.Length > 0 ? "~":"") + row.Cells[0].Value.ToString();
		foreach(string s in emaillist.Split('~'))
		{
			for(int i = 0; i < dt.Rows.Count; i++)
			{
				if((string)dt.Rows[i]["Email"] == s)dt.Rows[i].Delete();
			}
		}
        String catUsers = "";
        foreach (DataRow r in dt.Rows)
		{
            if (((string)r["Email"]).Length > 0)catUsers += (catUsers.Length > 0 ? "~":"") + (string)r["Email"];
		}
        edvReasonDetail.dataView[edvReasonDetail.Row][el] = catUsers;
	}


DataTable DMR_CT, DMR_TX, DMR_MX, DMR_UK, DMR_PL, DMR_Other;
	private void edvReasonDetail_EpiViewNotification(EpiDataView view, EpiNotifyArgs args)
	{
		// ** Argument Properties and Uses **
		// view.dataView[args.Row]["FieldName"]
		// args.Row, args.Column, args.Sender, args.NotifyType
		// NotifyType.Initialize, NotifyType.AddRow, NotifyType.DeleteRow, NotifyType.InitLastView, NotifyType.InitAndResetTreeNodes
		if ((args.NotifyType == EpiTransaction.NotifyType.Initialize))
		{
			if ((args.Row > -1))
			{
				//MessageBox.Show((string)edvReasonDetail.dataView[edvReasonDetail.Row]["Character01"]);
				InitializeDatagrid(ref DMR_CT, ref epiDMR_CT, "Character01");
				InitializeDatagrid(ref DMR_TX, ref epiDMR_TX, "Character02");
				InitializeDatagrid(ref DMR_MX, ref epiDMR_MX, "Character03");
				InitializeDatagrid(ref DMR_UK, ref epiDMR_UK, "Character04");
				InitializeDatagrid(ref DMR_PL, ref epiDMR_PL, "Character05");
				InitializeDatagrid(ref DMR_Other, ref epiDMR_Other, "Character06");
			}
			else
			{
				ClearAll(ref DMR_CT);
				ClearAll(ref DMR_TX);
				ClearAll(ref DMR_MX);
				ClearAll(ref DMR_UK);
				ClearAll(ref DMR_PL);
				ClearAll(ref DMR_Other);
			}
		}
	}
	private void ClearAll(ref DataTable dt)
	{
		if(dt != null)
		while(dt.Rows.Count > 0)dt.Rows[0].Delete();
	}
	private void InitializeDatagrid(ref DataTable dt, ref EpiUltraGrid dg, string el)
	{
		dt = new DataTable();
		dt.Columns.Add(new DataColumn("Email", typeof(string)));
		oTrans.PushStatusText((string)edvReasonDetail.dataView[edvReasonDetail.Row][el], false);
		foreach(string e in ((string)edvReasonDetail.dataView[edvReasonDetail.Row][el]).Split('~'))
		{
			if(e == string.Empty)continue;
			DataRow r = dt.NewRow();
			r["Email"] = e;
			dt.Rows.Add(r);
		}
		dg.DataSource = dt;
	}

